use strict"

function positivePowers(b, e) {
    let result = 1;
    if (e <= 0) return result;
    for (let i = 1; i <= e; i++) {
        result *= b;
    }
    return result;
}

let base = 2;
let exp = 3;
let test = positivePowers(base, exp);
console.log(test);

