"use strict"

function zeroArray(m, n) {
    // Creates a 2-D array with m rows and n columns of zeroes
    let newArray = [];    
    for (let i = 0; i < m; i++) {
        // Adds m rows into newArray
        let row = [];
        for (let j = 0; j < n; j++) {
            // Adds n zeroes into the current row
            row.push(0);
        }
        newArray.push(row);
    }
    return newArray;
}

let test = zeroArray(3, 2);
console.log(test);

